mapper.select:
select no, email, nickname, name, profile_img as profileImg, addrs
from ffp.user
where no = #{no}

select no, email, nickname, name, profile_img as profileImg, addrs
from ffp.user
where email = #{email}

select count(*)
from ffp.user
where email = #{email}

select count(*)
from ffp.user
where nickname = #{nickname}

mapper.update:
update ffp.user
set nickname = #{nickname}, name = #{name}, addrs = #{addrs}
where no = #{no}

UPDATE ffp.user
SET refresh_token = #{refreshToken}
WHERE email = #{email}

mapper.select:
select refresh_token
from ffp.user
where email = #{email}
